mapper.select:
SELECT *
	FROM saveEventArticle
	WHERE memberId = #{loginedMemberId} 
	AND relId = #{relId}
	AND relTypeCode = #{relTypeCode}

SELECT EA.* , IFNULL(SEA.save, 0) AS save FROM event_Article AS EA LEFT JOIN (SELECT * FROM saveEventArticle WHERE memberId = #{loginedMemberId} ) AS SEA ON EA.id = SEA.relId WHERE SEA.save = 1
AND EA.progress = #{progress}
AND EA.progress = #{progress}
AND ( EA.name LIKE CONCAT('%', #{searchKeyword}, '%') OR EA.goods LIKE CONCAT('%', #{searchKeyword}, '%') OR EA.needs LIKE CONCAT('%', #{searchKeyword}, '%') OR EA.price LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY SEA.regDate DESC

SELECT COUNT(*) FROM event_Article AS EA LEFT JOIN (SELECT * FROM saveEventArticle WHERE memberId = #{loginedMemberId} ) AS SEA ON EA.id = SEA.relId WHERE SEA.save = 1
AND EA.progress = #{progress}
AND EA.progress = #{progress}
AND ( EA.name LIKE CONCAT('%', #{searchKeyword}, '%') OR EA.goods LIKE CONCAT('%', #{searchKeyword}, '%') OR EA.needs LIKE CONCAT('%', #{searchKeyword}, '%') OR EA.price LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY SEA.regDate DESC

mapper.delete:
DELETE FROM saveEventArticle
	WHERE memberId = #{loginedMemberId} 
	AND relId = #{relId}
	AND relTypeCode = #{relTypeCode}
